CREATE TABLE sys_property (ID_ varchar(50) NOT NULL, CATEGORY_ varchar(200), DESCRIPTION_ longtext, INITVALUE_ longtext, INPUTTYPE_ varchar(50), LOCKED_ int, MAXVALUE_ double, MINVALUE_ double, NAME_ varchar(50), TITLE_ varchar(200), TYPE_ varchar(50), VALUE_ longtext, PRIMARY KEY (ID_)) ENGINE=InnoDB DEFAULT CHARSET=utf8 DEFAULT COLLATE=utf8_bin;
