mapper.delete:
delete from SYS_SQL_CRITERIA
where (
ID_ IN

	#{x_id}

)

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.SORTNO_ asc

mapper.sql:
from SYS_SQL_CRITERIA E


	1 = 1


		and E.PARENTID_ = #{parentId}



		and E.PARENTID_ IN

			#{x_parentId}




		and E.TENANTID_ = #{tenantId}



		and E.NAME_ = #{name}



		and E.NAME_ like #{nameLike}



		and E.MODULEID_ = #{moduleId}



		and E.BUSINESSKEY_ = #{businessKey}



		and E.TREEID_ like #{treeIdLike}



		and E.LOCKED_ = #{locked}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}
