mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.ACTIVE_ desc, E.SORTNO_ asc, E.CREATETIME_ desc

mapper.sql:
from SYS_EXPORT E


	1 = 1


		and E.NODEID_ = #{nodeId}



		and E.NODEID_ IN

			#{x_nodeId}




		and E.DEPLOYMENTID_ = #{deploymentId}



		and E.DEPLOYMENTID_ IN

			#{x_deploymentId}




		and E.TITLE_ like #{titleLike}



		and E.SYNCFLAG_ = #{syncFlag}



		and E.TYPE_ = #{type}



		and E.TEMPLATEID_ = #{templateId}



		and E.ACTIVE_ = #{active}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}



		and E.UPDATEBY_ = #{updateBy}



		and E.UPDATETIME_ >= #{updateTimeGreaterThanOrEqual}



		and E.UPDATETIME_ <= #{updateTimeLessThanOrEqual}
