mapper.select:
select E.*



		order by ${orderBy}


		order by E.SORTNO_ asc

mapper.sql:
from SYS_EXPORT_ITEM E


	1 = 1


		and E.EXPID_ = #{expId}



		and E.EXPID_ IN

			#{x_expId}




		and E.DEPLOYMENTID_ = #{deploymentId}



		and E.DEPLOYMENTID_ IN

			#{x_deploymentId}




		and E.LOCKED_ = #{locked}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}
